const style = `
  dialog {
    font-family: var(--type-body);
    padding: 0;
    box-shadow: 3px 3px rgba(0,0,0,0.1);
    border: 0 none;
    min-width: 280px;
    max-width: 560px;
    left: 50%;
    right: auto;
    transform: translate(-50%, 0);
    transition: opacity 1s ease-in-out;
  }

  dialog[open]::backdrop {
    animation: backdrop-fade 1s ease forwards;
  }

  @keyframes backdrop-fade {
    from {
      background: transparent;
    }
    to {
      background: rgba(0,0,0,0.1);
    }
  }

  dialog header {
    background: none;
    border-right: 0;
    padding: 0.5em;
  }

  dialog nav {
    display: flex;
    flex-direction: column;
    padding: 0.5em;
  }

  dialog nav#social-links a.button {
    border: 0 none;
    justify-content: space-between;
  }

  dialog nav .button:hover {
    background-color: rgba(var(--accent-color), var(--opacity-hover));
  }

  dialog nav .button:active {
    background-color: rgba(var(--accent-color), var(--opacity-active));
  }

  dialog footer {
    text-align: right;
    padding: 0.5em;
  }
`

export function links_dialog(spec) {
  let { _root } = spec;
  const _web_component = web_component(spec);
  const _state = state(spec);

  const init = () => {}

  const effects = () => {
    const dialog = _root.shadowRoot.querySelector('dialog');
    const cancel_button = _root.shadowRoot.querySelector('#cancel-btn');

    dialog.addEventListener('click', (event) => {
      if (event.target === dialog) {
        event.preventDefault();
        close();
      }
    });

    cancel_button.addEventListener('click', (event) => {
      close();
    });
  }

  const show = () => {
    _root.shadowRoot.querySelector('dialog').showModal();
  }

  const close = () => {
    _root.shadowRoot.querySelector('dialog').close();
  }

  return Object.freeze({
    ..._web_component,
    effects,
    show,
    close
  })
}

